from region_path_generator import RegionPathGenerator


class LayoutFromGrid:
    """ä»è¾“å…¥ç½‘æ ¼åˆ›å»ºå¸ƒå±€ç®¡ç†å™¨"""

    def __init__(self, grid):
        self.grid = np.array(grid)
        self.rows, self.cols = self.grid.shape

    def get_boundary_points(self):
        """
        ä½¿ç”¨OpenCVçš„findContourså‡½æ•°æå–æœ‰æ•ˆåŒºåŸŸçš„å¤–è¾¹ç•Œç‚¹
        """
        # åˆ›å»ºäºŒå€¼å›¾åƒ (0è¡¨ç¤ºéšœç¢ç‰©ï¼Œ255è¡¨ç¤ºå¯é€šè¡ŒåŒºåŸŸ)
        binary_map = (1 - self.grid) * 255
        binary_map = binary_map.astype(np.uint8)

        # ä½¿ç”¨findContourså‡½æ•°æå–è½®å»“
        contours, hierarchy = cv2.findContours(binary_map, cv2.RETR_EXTERNAL, cv2.CHAIN_APPROX_SIMPLE)

        # å¦‚æœæ‰¾åˆ°å¤šä¸ªè½®å»“ï¼Œé€‰æ‹©æœ€å¤§çš„ä¸€ä¸ªï¼ˆå¤–è¾¹ç•Œï¼‰
        if contours:
            # æŒ‰é¢ç§¯æ’åºï¼Œå–æœ€å¤§çš„è½®å»“
            main_contour = max(contours, key=cv2.contourArea)

            # å°†è½®å»“ç‚¹è½¬æ¢ä¸ºåˆ—è¡¨æ ¼å¼ [(x,y), (x,y), ...]
            # OpenCVçš„contouræ ¼å¼æ˜¯[[[x,y]], [[x,y]], ...]
            boundary_points = [tuple(point[0]) for point in main_contour]

            # é—­åˆè½®å»“ï¼ˆç¡®ä¿é¦–å°¾ç›¸è¿ï¼‰
            if boundary_points and boundary_points[0] != boundary_points[-1]:
                boundary_points.append(boundary_points[0])

            return boundary_points
        else:
            return []


def solve_path(input_grid, num_regions=10):
    """
    è¾“å…¥å›¾çš„æ±‚è§£å™¨å‡½æ•°

    å‚æ•°:
        input_grid: äºŒç»´JSONæ•°ç»„ (åˆ—è¡¨çš„åˆ—è¡¨)ï¼Œ0è¡¨ç¤ºå¯é€šè¡ŒåŒºåŸŸï¼Œ1è¡¨ç¤ºéšœç¢ç‰©/è¾¹ç•Œ

    è¿”å›:
        JSONæ ¼å¼çš„è·¯å¾„æ•°ç»„ï¼š[è·¯å¾„1, è·¯å¾„2, ...]
        æ¯ä¸ªè·¯å¾„æ˜¯åæ ‡ç‚¹åˆ—è¡¨ï¼š[[x1,y1], [x2,y2], ...]
    """
    # å°†è¾“å…¥è½¬æ¢ä¸ºnumpyæ•°ç»„
    grid = np.array(input_grid)
    rows, cols = grid.shape

    # åˆ›å»ºå¸ƒå±€ç®¡ç†å™¨
    layout_manager = LayoutFromGrid(grid)

    # åˆ›å»ºåŒºåŸŸåˆ’åˆ†å™¨
    divider = RegionDivider(layout_manager)

    # ç”Ÿæˆåˆ†éš”çº¿
    vertical_dividers = divider.generate_vertical_dividers(num_regions=np.ceil(num_regions/2))
    horizontal_dividers = divider.generate_horizontal_dividers(vertical_dividers)

    # åˆ›å»ºè·¯å¾„ç«¯ç‚¹ç”Ÿæˆå™¨
    endpoint_generator = PathEndpointGenerator(layout_manager, divider)

    # ç”Ÿæˆæ‰€æœ‰åŒºåŸŸçš„è·¯å¾„ç«¯ç‚¹
    all_endpoints = endpoint_generator.generate_endpoints_for_all_regions(
        vertical_dividers, horizontal_dividers)

    # åˆ›å»ºåŒºåŸŸè·¯å¾„ç”Ÿæˆå™¨
    region_path_generator = RegionPathGenerator(layout_manager, divider, endpoint_generator)

    # ä¸ºæ‰€æœ‰å­åŒºåŸŸç”Ÿæˆè·¯å¾„
    paths = region_path_generator.generate_all_region_paths(
        vertical_dividers, horizontal_dividers, all_endpoints)

    # è½¬æ¢ä¸ºæ‰€éœ€çš„è¾“å‡ºæ ¼å¼ - æ³¨æ„åæ ‡è½¬æ¢
    result = []
    for path_info in paths:
        # åŸä»£ç ä¸­è·¯å¾„ç‚¹æ ¼å¼ä¸º(y,x)ï¼Œéœ€è¦è½¬æ¢ä¸º[x,y]
        path_points = [[x, y] for y, x in path_info['path']]
        result.append(path_points)

    return result


def visualize_grid_and_paths(input_grid, paths, title="è·¯å¾„è§„åˆ’ç»“æœå¯è§†åŒ–"):
    """
    å¯è§†åŒ–ç½‘æ ¼å’Œè·¯å¾„

    å‚æ•°:
        input_grid: è¾“å…¥ç½‘æ ¼ï¼Œ0è¡¨ç¤ºå¯é€šè¡Œï¼Œ1è¡¨ç¤ºéšœç¢ç‰©
        paths: è·¯å¾„åˆ—è¡¨ï¼Œæ¯ä¸ªè·¯å¾„æ˜¯ä¸€ç³»åˆ—[x,y]åæ ‡
        title: å›¾è¡¨æ ‡é¢˜
    """
    # å°†è¾“å…¥ç½‘æ ¼è½¬æ¢ä¸ºnumpyæ•°ç»„
    grid = np.array(input_grid)

    sns.set(font="SimHei")  # è®¾ç½®å­—ä½“
    # åˆ›å»ºç”»å¸ƒ
    fig, ax = plt.subplots(figsize=(12, 10))

    # è®¾ç½®é¢œè‰²æ˜ å°„: ç™½è‰²ä¸ºå¯é€šè¡ŒåŒºåŸŸï¼Œç°è‰²ä¸ºéšœç¢ç‰©
    cmap = ListedColormap(['white', 'grey'])

    # ç»˜åˆ¶ç½‘æ ¼åº•å›¾
    ax.imshow(grid, cmap=cmap, interpolation='nearest')

    # æ·»åŠ ç½‘æ ¼çº¿
    ax.grid(which='both', color='lightgrey', linestyle='-', linewidth=0.5)

    # è®¾ç½®æ¯ä¸ªç½‘æ ¼å•å…ƒæ˜¾ç¤ºåæ ‡
    ax.set_xticks(np.arange(-0.5, grid.shape[1], 1))
    ax.set_yticks(np.arange(-0.5, grid.shape[0], 1))
    ax.set_xticklabels([])
    ax.set_yticklabels([])


    # ä¸ºæ¯ä¸ªåŒºåŸŸçš„è·¯å¾„è®¾ç½®ä¸åŒé¢œè‰²
    colors = ['blue', 'green', 'red', 'purple', 'orange', 'brown']

    # ç»˜åˆ¶æ¯æ¡è·¯å¾„
    legend_elements = []
    for i, path in enumerate(paths):
        color = colors[i % len(colors)]

        # å°†è·¯å¾„ç‚¹è½¬æ¢ä¸ºnumpyæ•°ç»„ä»¥ä¾¿ç»˜åˆ¶
        path_array = np.array(path)

        # ç”»çº¿è¿æ¥è·¯å¾„ç‚¹
        ax.plot(path_array[:, 0], path_array[:, 1], color=color, linewidth=3,
                marker='o', markersize=5, label=f'è·¯å¾„ {i + 1}')

        # æ ‡è®°èµ·ç‚¹å’Œç»ˆç‚¹
        ax.plot(path_array[0, 0], path_array[0, 1], 'g*', markersize=15)
        ax.plot(path_array[-1, 0], path_array[-1, 1], 'ro', markersize=12)

    # è®¾ç½®æ ‡é¢˜
    ax.set_title(title, fontsize=16)

    # æ·»åŠ æ³¨é‡Š
    # ax.text(0.01, 0, "â­ èµ·ç‚¹", transform=ax.transAxes, fontsize=12, color='green')
    # ax.text(0.12, 0, "ğŸ”´ ç»ˆç‚¹", transform=ax.transAxes, fontsize=12, color='red')
    # ax.text(0.20, 0, "â¬œ å¯é€šè¡ŒåŒºåŸŸ", transform=ax.transAxes, fontsize=12)
    # ax.text(0.35, 0, "â¬› éšœç¢ç‰©", transform=ax.transAxes, fontsize=12, color='grey')

    # æ·»åŠ åæ ‡è½´æ ‡ç­¾
    ax.set_xlabel('åˆ— (X)', fontsize=12)
    ax.set_ylabel('è¡Œ (Y)', fontsize=12)

    # è°ƒæ•´å¸ƒå±€
    plt.tight_layout()

    # æ˜¾ç¤ºå›¾è¡¨
    plt.show()

    return fig, ax

